import React from 'react'
import { useSelector } from 'react-redux'

export default function LastMove() {
    const { selected, lastMove } = useSelector((store) => store.chess)

	const { x1, y1 } = selected || {}
	const { x2, y2 } = lastMove || {}

    const styles = (x, y) => ({
        visibility: `${x && y ? 'visible' : 'hidden'}`,
		top: `${y * 60}px`,
		left: `${x * 60}px`,
    })

	return (
		<>
			<div style={styles(x1, y1)} className='from'></div>
			<div style={styles(x2, y2)} className='to'></div>
		</>
	)
}
