import React from 'react'
import { useSelector } from 'react-redux'

export default function LastMove() {
    const { selected, lastMoves } = useSelector((store) => store.chess)
    
    const {x1, y1} = selected || {}
	
    const highLighted = [[x1, y1], ...lastMoves]

	const styles = (x, y) => ({
		visibility: `${!isNaN(x) && !isNaN(y) ? 'visible' : 'hidden'}`,
		top: `${y * 60}px`,
		left: `${x * 60}px`,
	})

	return highLighted.map(([x, y]) => (
		<div
			style={styles(x, y)}
			className='lastMove'
			key={`${x}${y}lastmove`}
		></div>
	))
}
