import {nextMoves} from "../NextMoves";
import { useSelector, useDispatch } from 'react-redux';
import { setNextMoves } from '../../../../../../features/chess/chessSlice';

export default function useGetRookMoves(x, y){

    const { gameField } = useSelector(store => store.chess)
    const dispatch = useDispatch()

    if (gameField[y][x][1] !== "R")
        return () => {}

    const moves = []

    for (let i = 1; x + i < 8; i++){
        moves.push([x + i, y])
        if (gameField[y][x + i] !== "0"){break}
    }
    for (let i = 1; x-i>=0; i++) {
        moves.push([x - i, y])
        if (gameField[y][x - i] !== "0") {break}
    }
    for (let i = 1; y+i<8; i++){
        moves.push([x, y + i])
        if (gameField[y + i][x] !== "0"){break}
    }
    for (let i = 1; y-i>=0; i++){
        moves.push([x, y - i])
        if (gameField[y - i][x] !== "0"){break}
    }

    return () => {
        return moves
    }

}
