import React from 'react'
import { useSelector } from 'react-redux'

export default function LastMove() {
	const { selected, lastMove } = useSelector((store) => store.chess)

	const styles = (x, y) => ({
		visibility: `${!isNaN(x) && !isNaN(y) ? 'visible' : 'hidden'}`,
		top: `${y * 60}px`,
		left: `${x * 60}px`,
	})

	return lastMove.map(([x, y]) => (
		<div style={styles(x1, y1)} className='.lastMove'></div>
	))
}
