const turns = {
	b: 'w',
	w: 'b',
}


const reducers = {

	clearNextMoves: (state) => {
		state.nextMoves = []
	},
	setNextMoves: (state, { payload }) => {
		state.nextMoves = payload
	},
	setCheck: (state, { payload }) => {
		state.check = payload
	},
	setTurn: (state, { payload }) => {
		state.turn = payload
	},
	setSelected: (state, { payload }) => {
		state.selected = payload
	},
	setFollowing: (state, { payload }) => {
		state.followingPiece = payload
	},
	setPromoted: (state, { payload }) => {
		if (!payload) {
			const { x1, y1, name } = state.promoted
			state.gameField[y1][x1] = name
			state.promoted = payload
			state.turn = turns[state.turn]
		} else if (payload.length > 1) {
			state.promoted = payload
		} else {
			const { x2, y2 } = state.promoted
			state.gameField[y2][x2] = payload.name
			state.promoted = null
		}
	},
	setCoverMoves: (state, { payload }) => {
		state.coverMoves = payload
	},
	movePiece: (state, { payload }) => {
		const [x2, y2] = payload
		const { x1, y1, name } = state.selected
		const [color, piece] = name

		if (piece === 'K' && Math.abs(x2 - x1) > 1) {
			const k = x2 > x1 ? 1 : -1
			state.gameField[y1][4 + k] = color + 'R'
			state.gameField[y1][4 + 2 * k] = color + 'K'
			state.gameField[y1][4] = '0'
			state.gameField[y1][k > 0 ? 7 : 0] = '0'
		} else if (
			piece === 'P' &&
			x1 !== x2 &&
			state.gameField[y2][x2] === '0'
		) {
			state.gameField[y1][x2] = '0'
			state.gameField[y2][x2] = name
			state.gameField[y1][x1] = '0'
		}

		if (piece === 'P' && (y2 === 7 || y2 === 0)) {
			state.promoted = { x1, y1, x2, y2, name }
			state.gameField[y1][x1] = '0'
		} else {
			state.gameField[y2][x2] = name
			state.gameField[y1][x1] = '0'
		}

		if (
			piece === 'R' &&
			(x1 === 0 || x1 === 7) &&
			!state.castlingMoved.includes(x1 + name)
		) {
			state.castlingMoved.push(x1 + name)
		} else if (
			piece === 'K' &&
			x1 === 4 &&
			!state.castlingMoved.includes(name)
		) {
			state.castlingMoved.push(name)
		}

		state.enPassing =
			name[1] === 'P' && Math.abs(y2 - y1) === 2 ? { x2, y2 } : null

		state.turn = turns[state.turn]
		state.selected = null
		state.coverMoves = []
		state.nextMoves = []
	},
}

export default reducers
