import { useDispatch } from 'react-redux'
import { setNextMoves } from '../../../../../../features/chess/chessSlice'


export default function useGetKnightMoves(x, y) {
    
    const dispatch = useDispatch()

        if (gameField[y][x][1] !== "P")
        return () => {}

    const moves = [
                [x + 2, y - 1], [x + 2, y + 1],
                [x - 2, y + 1], [x - 2, y - 1],
                [x + 1, y + 2], [x - 1, y + 2],
                [x + 1, y - 2], [x - 1, y - 2]
    ]
    return dispatch(setNextMoves(moves))
}
