// import nextMovesInclude from "../Logic/Next moves/Nextmoves include";
// import movePiece from "../Logic/Move piece/Move piece";
import followPointer from "./Active motion";

export default function unFollow(payload, event) {
    
    const {target, startingX, startingY, x, y} = payload

    let newX = x + Math.round((event.x - startingX) / 60)
    let newY = y + Math.round((event.y - startingY) / 60)

    // if (nextMovesInclude([newX, newY])){
    //     movePiece(newX, newY)
    // }

    target.style.transition = "170ms"
    target.style.zIndex = "2"

    target.style.top = `${y * 60}px`
    target.style.left = `${x * 60}px`

    window.removeEventListener("mousemove", event => followPointer(payload, event))
    window.removeEventListener("mouseup", event => unfollow(payload, event))
}
