import {useContext} from "react";
import PromotionPiece from "./Promotion piece";
import {PromotedContext} from "./Promotion context";

export let promoted, setPromoted

function getChoices([x, y]) {
	if (y === 0) {
		return (x < 7)?  ['Q', 'N', 'B', 'R']:['N', 'Q', 'R', 'B']
	} else if (y === 7) {
		if (x < 7) return ['B', 'R', 'Q', 'N']
		else return ['R', 'B', 'N', 'Q']
	}
}

export default function Promotion(){

    [promoted, setPromoted] = useContext(PromotedContext)

    if (!promoted) return

    const [x, y] = promoted
    const choices = getChoices([x, y])

    const styles = {
        left: `${(x < 7? x : x - 1) * 60}px`,
        top: `${(y < 7? y: y - 1) * 60}px`
    }

    return (
        <>
            <div className={"shadow"}></div>
            <div className={"promotion"}
                 style={styles}>
                {choices.map((piece, id) =>
                    <PromotionPiece
                        key={piece}
                        name={piece}
                        index={id}/>
                )}
            </div>
        </>
    )
}
