import unfollow from "./Cancel motion";
import followPointer from "./Active motion";


const follow = (event, { x, y, startingX, startingY }) => {
    
    const target = event.target
    console.log(x, startingX-event.x)
    target.style.top = `${y * 60 + event.y - startingY}px`
    target.style.left = `${x * 60 + event.x - startingX}px`
}

const unFollow = (event, {x, y, startingX, startingY }) => {

    let newX = x + Math.round((event.x - startingX) / 60)
    let newY = y + Math.round((event.y - startingY) / 60)

    const target = event.target

    // if (nextMovesInclude([newX, newY])){
    //     movePiece(newX, newY)
    // }

    target.style.transition = "170ms"
    target.style.zIndex = "2"

    target.style.top = `${y * 60}px`
    target.style.left = `${x * 60}px`

    window.removeEventListener("mousemove", func1)
    window.removeEventListener("mousemove", func2)
}


const func1 = event => follow(event, { x, y, startingX, startingY })
const func2 = event => unFollow(event, { x, y, startingX, startingY })


export default function startFollowing(event, x, y){

    const startingX = event.clientX
    const startingY = event.clientY

    event.target.style.transition = "none"
    event.target.style.zIndex = "3"

    window.addEventListener("mousemove", func1)
    window.addEventListener("mouseup", func2)
}
