import React from 'react'
import { useSelector } from 'react-redux'

export default function LastMove() {

    const { lastMove } = useSelector((store) => store.chess)
    
    if (!lastMove) return

	const { x1, y1} = lastMove

	const styles1 = {
		top: `${y1 * 60}px`,
		left: `${x1 * 60}px`,
	}
	const styles2 = {
		top: `${y2 * 60}px`,
		left: `${x2 * 60}px`,
	}

	return (
		<>
			<div style={styles1} className='from'></div>
            <div style={styles2} className='to'></div>
		</>
	)
}
