import unfollow from "./Cancel motion";
import followPointer from "./Active motion";

export let x, y, startingX, startingY
export let following, clearFollowing = () => following = false

export default function startFollowing(event, x, y){

    console.log(event.target)

    startingX = event.clientX
    startingY = event.clientY

    event.target.style.transition = "none"
    event.target.style.zIndex = "3"

    const payload = {
        target: event.target,
        startingX, x,
        startingY, y,
    }

    const follow = followPointer(payload, event)

    window.addEventListener("mousemove", event => follow)
    window.addEventListener("mouseup", ()=>window.removeEventListener("mousemove", follow))
}
