import React from 'react'
import { useSelector } from 'react-redux'

export default function LastMove() {
    const { lastMove } = useSelector((store) => store.chess)

	const styles = (x, y) => ({
		visibility: `${!isNaN(x) && !isNaN(y) ? 'visible' : 'hidden'}`,
		top: `${y * 60}px`,
		left: `${x * 60}px`,
	})

	return (
		<>
			<div style={styles(x1, y1)} className='.lastMove'></div>
			<div style={styles(x2, y2)} className='.lastMove'></div>
		</>
	)
}
